  void nextHealth() {
    if (_healthNumber < _healthBank.length - 1) {
      _healthNumber++;
    }
  }

  void previousHealth() {
    if (_healthNumber != 0) {
      _healthNumber--;
    }
  }

  String getHealthText() {
    return _healthBank[_healthNumber].healthText;
  }

  bool isFinished() {
    if (_healthNumber >= _healthBank.length - 1) {
      print('Now returning true');
      return true;
    } else {
      return false;
    }
  }

  void reset() {
    _healthNumber = 0;
  }
