  void nextMotivate() {
    if (_motivateNumber < _motivateBank.length - 1) {
      _motivateNumber++;
    }
  }

  void previousMotivate() {
    if (_motivateNumber != 0) {
      _motivateNumber--;
    }
  }

  String getMotivateText() {
    return _motivateBank[_motivateNumber].motivateText;
  }

  bool isFinished() {
    if (_motivateNumber >= _motivateBank.length - 1) {
      print('Now returning true');
      return true;
    } else {
      return false;
    }
  }

  void reset() {
    _motivateNumber = 0;
  }
